
public class Sess1_Assigmnt3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20, b = 10;
		System.out.println("Before Swapping: a: "+a+"\tb: "+b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println("After  Swapping: a: "+a+"\tb: "+b);
	}

}
